WHERE A.YDQY_id = yjinfo.YDQY_id AND A.DF_id = yjinfo.DF_id AND A.JCD_Type IN (12,14)) > 0 THEN (SELECT LastSD FROM ynyjjc.yj_info A WHERE A.YDQY_id = yjinfo.YDQY_id AND A.DF_id = yjinfo.DF_id AND A.JCD_Type IN (12,14) LIMIT 1) END) AS char), '') AS humidity, -- 湿度 MAX(yjinfo.mStatus) mstatus, IF(MAX(yjinfo.mStatus) = 0, '未生效', IF(MAX(yjinfo.mStatus) = 1, '已生效', IF(MAX(yjinfo.mStatus) = -1, '通信异常', IF(MAX(yjinfo.mStatus) = -1, '已删除', '其他')))) mstatusStr, (SELECT CAST(COUNT(1) AS char) FROM ${tableNameString} jg8 WHERE jg8.mstatus = 0 AND jg8.DF_id = yjinfo.DF_id) warningCount, IFNULL((SELECT max(jg8.GJ_CLASSTYPE) FROM ${tableNameString} jg8 WHERE jg8.mstatus = 0 AND jg8.DF_id = yjinfo.DF_id),'') warningMaxLevel FROM yj_info yjinfo WHERE 1=1
AND yjinfo.WBGS_id= #{ynBaseStaffCompanyId,jdbcType=VARCHAR}
AND yjinfo.YDQY_id= #{ynBaseCustomerEnterpriseId,jdbcType=VARCHAR}
